    _builder.append("\t\t");
    _builder.append("<h2>Installation Instructions</h2>");
    _builder.newLine();
    _builder.append("\t\t");
    _builder.append("<hr>");
    _builder.newLine();
    _builder.append("\t\t");
    _builder.append("<div class=\"span1\">&nbsp;</div>");
