    _builder.append("</div>");
    _builder.newLine();
    _builder.append("\t\t\t\t\t");
    _builder.append("</p>");
    _builder.newLine();
    _builder.append("\t\t\t\t\t");
    _builder.append("</section>");
    _builder.newLine();
    _builder.append("\t\t\t\t\t");
    _builder.append("<section>");
    _builder.newLine();
    _builder.append("\t\t\t\t\t");
    _builder.append("<h2 style=\"padding-top: 15px;\">Getting the runtime library via Maven</h2>");
    _builder.newLine();
    _builder.append("\t\t\t\t\t");
    _builder.append("<p>");
    _builder.newLine();
    _builder.append("\t\t\t\t\t\t");
    _builder.append("The <a href=\"api/2.4.0/index.html\">runtime library</a> is available from Maven Central, too: <a href=\"http://repo.maven.apache.org/maven2\">http://repo.maven.apache.org/maven2</a>.");
    _builder.newLine();
    _builder.append("\t\t\t\t\t");
    _builder.append("</p>");
