private static StringBuffer checkParens(StringBuffer buffer) {
        if(buffer.length()==0) {
            return buffer;
        }
        if ((buffer.charAt(0) != '(') || (buffer.charAt(buffer.length() - 1) != ')')) {
            buffer.append(")");
            buffer.insert(0, "(");
        }
        return buffer;
    }

