	      		sqlEx.printStackTrace();
			}
      		} 
		finally 
		{
			if (resultset != null) 
			{
          		try  
			{ 
				resultset.close(); 
			} 
			catch (SQLException ignore) {}
          		resultset = null;
        		}
		if (statement != null) 
			{
          		try  
			{ 
			statement.close(); 
			} 
			catch (SQLException ignore) {}
          		statement = null;
        		}
        	if (con != null) 
			{
          		try  
			{ 
			con.close(); 
			} 
			catch (SQLException ignore) {}
          		con = null;
        		}	
      		}
    	}
    	return status;
  }

