private String hostPath(String path) {
        final URI baseUri = uriInfo.getBaseUri();
        final int port = baseUri.getPort();

        return baseUri.getScheme() + "://" + baseUri.getHost() + (port != 80 ? ":" + Integer.toString(port) : "") + path;
    }

