        Set<Org> allowedOrgs = new HashSet<Org>();
        IssSlave s = IssFactory.lookupSlaveById(sid);
        for (String soid : soids) {
            Long oid = Long.parseLong(soid);
            Org anOrg = OrgFactory.lookupById(oid);
            allowedOrgs.add(anOrg);
