private void fillNew(int minimumCount) throws IOException {
        if (pos < count) {
            int remain = count - pos;
            System.arraycopy(buf, pos, buf, 0, remain);
            count = remain;
        } else {
            count = 0;
        }
        pos = 0;
        do {
            int n = in.read(buf, count, buf.length - count);
            if (n < 0) {
                throw new EOFException();
            }
            count += n;
        } while (count < minimumCount);
    }

