        int maxIndex = Math.min(offset + windowReach, quals.length - 1);
        for ( int iii = minIndex; iii < maxIndex; iii++ ) {
            if( quals[iii] < minQual ) {
                minQual = quals[iii];
            }
        }
        return minQual;
    }

