    if (this.isMatching() || this.isFIB() || this.isFIN())
    {
      if ( instruction != null)
        {
    	  helper.setItemText(instruction, this);
        }
    }
    ArrayList itemTexts = item.getItemTextArraySorted();

    setItemTexts(itemTexts);
    if (this.isTrueFalse()) // we know what the answers are (T/F)
