public static int hashIdentifier(@Nullable String s) {
    if (s == null) return 0;

    // not using String.hashCode because this way there's less collisions for short package names like 'com'
    int hash = 0;
    for (int i = 0; i < s.length(); i++) {
      hash = hash * 239 + s.charAt(i);
    }
    return hash;
  }

