            smtpOut.flush();

            //Get "DATA" response, should be "354 blah blah"
            smtpResult = smtpIn.readLine();
            if(!smtpResult.substring(0, 3).toString().equals("354"))
            {
                LOG.error("Error - SMTP DATA failed: " + smtpResult);
                return false;
            }

            //Send the Message
