protected static CloneIndex createIndex(List<Block>... blocks) {
    CloneIndex cloneIndex = new MemoryCloneIndex();
    for (List<Block> b : blocks) {
      for (Block block : b) {
        cloneIndex.insert(block);
      }
    }
    return cloneIndex;
  }

