        if (o != null) {
          //(check if field is static)
          if ((f.getModifiers() & Modifier.STATIC) == 0 && instances == null) {
            fatal("An instance object must be provided if an option is applied to a non-static field: " + c + "." + f);
          }
          //(required marker)
