protected ITextRegion createRegion(final List<INode> nodes) {
		// if we've got more than one ID elements, we want to select them all
		ITextRegion result = ITextRegion.EMPTY_REGION;
		for (INode node : nodes) {
			if (!isHidden(node)) {
				int length = node.getLength();
				if (length != 0)
					result = result.merge(new TextRegionWithLineInformation(node.getOffset(), length, node.getStartLine(), node.getEndLine()));
			}
		}
		return result;
	}

