private static void updateStats(HashMap map, Address key, int req, int rsp, int missing) {
        StatsEntry entry=(StatsEntry)map.get(key);
        if(entry == null) {
            entry=new StatsEntry();
            map.put(key, entry);
        }
        entry.xmit_reqs+=req;
        entry.xmit_rsps+=rsp;
        entry.missing_msgs_rcvd+=missing;
    }

