                }
                prop.put("remoteCrawlPeers_busy", pendicount);
                prop.put("remoteCrawlPeers_num", (availcount + pendicount));
            }

        }
        
        
        prop.put("crawler-paused",(switchboard.crawlingIsPaused())?0:1);
        
        // return rewrite properties
        return prop;
    }

