      case REFERENCE:
        return CellFinder.getCellForReference(editor, getNode(), myMessageTarget.getRole());
      case PROPERTY:
        return CellFinder.getCellForProperty(editor, getNode(), myMessageTarget.getRole());
      case DELETED_CHILD:
