        } catch (Throwable t) {
            throw new InstallerException(
                "Unexpected Problem during installation", t);

        } finally {

            // release temporary repositories
            this.removeRepositories(addedRepos);

            // release the lock, if we have it
            if (this.lock != null) {
                this.controller.releaseLock(this.lock);
                this.lock = null;
            }
        }
    }

