            if (node.getNodeType() == Node.ELEMENT_NODE && Namespaces.EXIST_NS.equals(node.getNamespaceURI())) {
                URLRewrite urw = parseAction(request, (Element) node);
                if (urw != null)
                    modelView.addView(urw);
            }
            node = node.getNextSibling();
        }
    }

