private static boolean pasteToTarget_internal(final SNode pasteTarget, final SNode pasteNode, final SNode anchorNode, String role, final boolean pasteBefore, boolean reallyPaste, final IOperationContext operationContext) {
    ConceptDeclaration pasteTargetType = (ConceptDeclaration) pasteTarget.getConceptDeclarationAdapter();
    ConceptDeclaration pasteNodeType = (ConceptDeclaration) pasteNode.getConceptDeclarationAdapter();
    final LinkDeclaration linkDeclaration = findMetalink(pasteTargetType, pasteNodeType, role);
    if (linkDeclaration == null) {
      return false;
    }
    if (reallyPaste) {
      CommandProcessor.instance().executeCommand(new Runnable() {
        public void run() {
          Cardinality cardinality = linkDeclaration.getSourceCardinality();
          boolean uniqueChild = (cardinality == Cardinality._0_1 || cardinality == Cardinality._1);
          if (linkDeclaration.getMetaClass() == LinkMetaclass.aggregation) {
            if (uniqueChild) pasteTarget.setChild(linkDeclaration.getRole(), pasteNode);
            else pasteTarget.insertChild(anchorNode, linkDeclaration.getRole(), pasteNode, pasteBefore);
          } else {
            if (uniqueChild) pasteTarget.insertReferent(anchorNode, linkDeclaration.getRole(), pasteNode, pasteBefore);
            else pasteTarget.setReferent(linkDeclaration.getRole(), pasteNode);
          }
        //Add resolving here!


      //  Resolver.resolveAllReferences(pasteNode, operationContext);

        }
      });
    }
    return true;
  }

