            stageIds.add(itemEntity.getStageId());
          }

          Set<Resource> stages = helper.getStageResources(clusterName, upgrade.getRequestId(), stageIds);

          aggregate(r, stages, requestPropertyIds);

          results.add(r);
        }
      }

    }

    return results;
  }

