private static Rectangle applyInsets(Rectangle rect, Insets i) {
    if (i == null) {
      return rect;
    }

    return new Rectangle(rect.x + i.left, rect.y + i.top, rect.width - (i.left + i.right), rect.height - (i.top + i.bottom));
  }

