            if (nodes != null) {

                String riakNodeName = member.getAttribute(RiakNode.RIAK_NODE_NAME);
                ((EntityInternal) member).setAttribute(RiakNode.RIAK_NODE_IN_CLUSTER, false);

                if (member.getAttribute(RiakNode.RIAK_NODE_IN_CLUSTER))
