		if (_new == null || old == null)
			return true;

		Collection<IEObjectDescription> oldEObjects = Lists.newArrayList(old.getExportedObjects());
		Collection<IEObjectDescription> newEObjects = Lists.newArrayList(_new.getExportedObjects());
