    else if (BREADTH_FIRST.equals(orderType)) {
      for (JavaArrangementMethodDependencyInfo rootInfo : info.getMethodDependencyRoots()) {
        setupBreadthFirstDependency(rootInfo);
      }
    }
    else {
      assert false : orderType;
    }
  }

