					propertySet.addProperty(DeadLocalStoreProperty.MANY_STORES);
				int occurrences = cfg.getLocationsContainingInstructionWithOffset(
						pc).size();
				if (occurrences > 2 || sourceLineAnnotation.getStartLine() > 0 && linesMentionedMultipleTimes.get(sourceLineAnnotation.getStartLine()))
