        }
        else if(Format.shortArray.equals(outputDataType))
        {
            /* Java short is always 2 bytes */
            dstLength = (dstLength % 2) + dstLength / 2;
            if(dst == null || ((short[])dst).length < dstLength)
                dst = new short[dstLength];
        }
        else
        {
