public static byte[] nonce() {
		byte [] nonce = new byte[32];
		boolean startsWithReserved;
		while (true) {
			startsWithReserved = false;
			_random.nextBytes(nonce);
			for (byte b: CCN_reserved_markers) {
				if (b == nonce[0]) {
					startsWithReserved = true;
					break;
				}
			}
			if (!startsWithReserved)
				break;
		}
		return nonce;
	}

