                            assertThat(response.getSuccessfulShards(), equalTo(2));
                            assertThat(response.getCount(), equalTo((long) numQueries));
                            assertThat(response.getMatches().length, equalTo(numQueries));
                        }
                    }
                } catch (Throwable t) {
                    logger.info("Error in percolate thread...", t);
                    run.set(false);
                    error.set(t);
                } finally {
                    done.countDown();
                }
            }
        };
        new Thread(r).start();

        try {
            for (int i = 0; i < 4; i++) {
                closeNode("node3");
                client().admin().cluster().prepareHealth()
                        .setWaitForEvents(Priority.LANGUID)
                        .setWaitForYellowStatus()
                        .setWaitForNodes("2")
                        .execute().actionGet();
                assertThat(error.get(), nullValue());
                closeNode("node2");
                client().admin().cluster().prepareHealth()
                        .setWaitForEvents(Priority.LANGUID)
                        .setWaitForYellowStatus()
                        .setWaitForNodes("1")
                        .execute().actionGet();
                assertThat(error.get(), nullValue());
                startNode("node3");
                client().admin().cluster().prepareHealth()
                        .setWaitForEvents(Priority.LANGUID)
                        .setWaitForYellowStatus()
                        .setWaitForNodes("2")
                        .execute().actionGet();
                assertThat(error.get(), nullValue());
                startNode("node2");
                client().admin().cluster().prepareHealth()
                        .setWaitForEvents(Priority.LANGUID)
                        .setWaitForYellowStatus()
                        .setWaitForNodes("3")
                        .execute().actionGet();
                assertThat(error.get(), nullValue());
            }
        } finally {
            run.set(false);
        }
        done.await();
        assertThat(error.get(), nullValue());
    }

