            int length = ignite.compute().call((IgniteCallable<Integer>)"Hello World"::length).get();

            System.out.println();
            System.out.println(">>> Computed length: " + length);

            System.out.println();
            System.out.println(">>> Check all nodes for hello message output.");
        }
    }

