     */
    private int getPriority(CmsMenuItemVisibilityMode mode) {

        if (mode.isPrioritized()) {
            return 4;
        }
        if (mode.isInActive()) {
            return 0;
        }
        if (mode.isActive()) {
            return 1;
        }
        // invisible
        return 2;
    }

