public Object get(Object key) {
        processLocalRecords();
        Record record = mapCache.get(key);
        if (record == null) {
            return OBJECT_REDO;
        } else {
            if (record.isActive()) {
                try {
                    Object value = toObject(record.getValue());
                    record.setLastAccessed();
                    return value;
                } catch (Throwable t) {
                    logger.log(Level.FINEST, "Exception when reading object ", t);
                    return OBJECT_REDO;
                }
            } else {
                //record is removed!
                mapCache.remove(key);
                return null;
            }
        }
    }

