            if (returnType != null) { // user selected a given type
                replaceWithLongerName(project, returnTypeRef, returnType);
                returnTypeRef = func.getReturnTypeRef();
                assert returnTypeRef != null;
                typeRefsToShorten.add(returnTypeRef);
            }
