    int[] sources =  FSUtils.getOneSpecificFileOrDir(context.data(), 
        curDirPre, dir);



    if(sources.length > 0) {
      for(int dirPre : sources) {
        if(FSUtils.isDir(context.data(), dirPre)) {
          FSUtils.printError(out, "mkdir", path, 17);
          return;
