        if(!(curDirPre == FSUtils.getROOTDIR())) {
          preNewFile = curDirPre + 5;
        }
        FSUtils.insert(context.data(), true, Token.token(dir), 
            Token.token(""), Token.token(0), 
            Token.token(System.currentTimeMillis()), curDirPre, preNewFile);
      } catch(Exception e) {
        e.printStackTrace();
      }
    }
  }

