					StringBuilder sb = new StringBuilder();

					sb.append(FileUtil.stripExtension(fileName));
					sb.append(StringPool.PERIOD);
					sb.append(targetExtension);

					fileName = sb.toString();

					is = convertedIS;

					converted = true;
				}
			}

