				}
			}
			catch (Exception e) {
				_log.error(e, e);
			}

			if (returnObj != null) {
				ObjectOutputStream oos =
					new ObjectOutputStream(res.getOutputStream());

				oos.writeObject(returnObj);

				oos.flush();
				oos.close();
			}
		}
		finally {
			try {
				PermissionCheckerFactory.recycle(permissionChecker);
			}
			catch (Exception e) {
			}
		}
	}

