    try {
      worker.process();
      // Start the lineage worker
      if (LineageUtils.isLineageEnabled(WorkerContext.getConf())) {
        lineageWorker.start();
      }

    } catch (Exception e) {
      LOG.error("Uncaught exception while running worker, shutting down and exiting.", e);
