@Override
    protected void componentShowing() {
        //logger.log(Level.INFO, "SHOWING");

        Mode mode = WindowManager.getDefault().findMode("floatingLeftBottom");
        if (mode != null) {
            TopComponent [] tcs = mode.getTopComponents();
            for (int i = 0; i<tcs.length; ++i) {
                if (tcs[i] == this)
                    //already floating
                    return;
            }
            mode.dockInto(this);
            this.open();
        }
    }

