      addPrimitive(new InsertAttribute(n, seq, -1));
      return Iter.EMPTY;
    }

    final DBNode dbn = (DBNode) n;
    final Data d = dbn.data;
    int p = dbn.pre;
    int l = p + d.attSize(p, d.kind(p));
    if(aSeq.size() > 0) ctx.updates.addPrimitive(
