public boolean visit(NumberLiteral node) {
      if (!node.getToken().endsWith("f") && !node.getToken().endsWith("d")) {
        for (int i = 0; i < node.getToken().length(); i++) {
          if (node.getToken().charAt(i) == '.') {
            String s = node.getToken() + "f";
            node.setToken(s);
            break;
          }
        }
      }
      return true;
    }

