                        if (child.getNodeType() == Node.ELEMENT_NODE
                                && child.getNamespaceURI().equals(NS)) {
                            if (child.getLocalName().equals("text")) {
                                StringBuffer buf = new StringBuffer();
                                Node next = child.getFirstChild();
                                while (next != null) {
