@Override
    public SolrDocument getDocumentById(final String id, final String ... fields) throws IOException {
        final SolrQuery query = new SolrQuery();
        assert id.length() == 12;
        // construct query
        query.setQuery("{!raw f=" + CollectionSchema.id.getSolrFieldName() + "}" + id);
        query.clearSorts();
        query.setRows(1);
        query.setStart(0);
        if (fields.length > 0) query.setFields(fields);
        query.setIncludeScore(false);

        // query the server
        try {
            final QueryResponse rsp = getResponseByParams(query);
            final SolrDocumentList docs = rsp.getResults();
            if (docs.isEmpty()) return null;
            return docs.get(0);
        } catch (final Throwable e) {
            throw new IOException(e.getMessage(), e);
        }
    }

