            String newHead = cb.doCommit();
            if (newHead.equals(revisionId)) {
                // 'empty' commit
                return newHead;
            }
            gate.commit(newHead);
            return newHead;
        } catch (Exception e) {
            throw new MicroKernelException(e);
        }
    }

