            "insert into \"part\".PURCHASE (_key, _val, id, personId, productId) values(?, ?, ?, ?, ?)")) {
            setObjectSmart(st, 1, p.key());
            setObjectSmart(st, 2, p);
            setObjectSmart(st, 3, p.id);
            setObjectSmart(st, 4, p.personId);
            setObjectSmart(st, 5, p.productId);

            st.executeUpdate();
        }
    }

