    File passFile = getPassFile();
    if (!passFile.isFile()) return;

    List<String> lines = CvsFileUtil.readLinesFrom(passFile, cvsRoot);
    try {
      CvsFileUtil.storeLines(lines, passFile);
    }
    catch (IOException e) {
      LOG.error(e);
    }
  }

