			return;
		}

		IndexWriterData writerData = _writerLookup.get(companyId);

		if (writerData != null) {
			decrement(writerData);
		}
		else {
			if (_log.isWarnEnabled()) {
				_log.warn("IndexWriterData not found for " + companyId);
			}
		}
	}

