private boolean checkNewFuzzyState(HTextFlowTarget textFlowTarget)
   {
      if (textFlowTarget == null)
      {
         return true;
      }
      else if (textFlowTarget.getState() == ContentState.New || textFlowTarget.getState() == ContentState.NeedReview)
      {
         return true;
      }
      return false;
   }

