            aveRating.setAverageVote(aveRating.getAverageVote() * 100 / getTotalReputation());
            totalReputation += aveRating.getAverageVote() - oldRep;
            return aveRating;
        } catch (RatingsException e) {
            throw new ReputationException(e);
        }
    }

