private <T> Set<T> getEntities(boolean oneEntity, T single, List<T> list) {
    Set<T> result = new HashSet<T>();

    if (single != null) {
      result.add(single);
    }

    if (oneEntity) {
      if (list != null && !list.isEmpty() && !(list.size() == 1 && list.contains(single))) {
        result.clear();
      }
    } else {
      if (list != null) {
        result.addAll(list);
      }
    }

    return result;
  }

